<style>
  .scrollable {
    height: 100%;
    overflow-y: scroll;
    scroll-behavior: smooth;
    padding: 40px 40px 60px;
  }
  .overlay {
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    background: #494b53cc;
    z-index: 4;
    opacity: 0;
    pointer-events: none;
    transition: all 0.4s;
  }

  .modal-open .overlay {
    opacity: 1;
    pointer-events: all;
  }

  h3 {
    font-size: 40px;
    line-height: 1;
    font-family: "Dela Gothic One";
    font-synthesis: none;
    margin: 1em 0;
    text-wrap: pretty;
  }

  h4 {
    font-size: 24px;
    line-height: 1;
    font-family: "Dela Gothic One";
    font-synthesis: none;
    text-wrap: pretty;
    margin: 2em 0 1em;
  }

  aside {
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    width: calc(100% - 24px);
    max-width: 552px;
    background: white;
    padding: 0;
    transform: translateX(calc(100% + 28px));
    pointer-events: none;
    user-select: none;
    transition: transform 0.4s;
    z-index: 5;
  }

  .modal-open aside {
    transform: translateX(0);
    pointer-events: all;
    user-select: all;
  }

  .button {
    background: white;
    white-space: nowrap;
    border: 1px solid #494b53;
    color: #494b53;
    height: 40px;
    width: 40px;
    border-radius: 999px;
    font-size: 14px;
    text-transform: uppercase;
    display: flex;
    justify-content: center;
    align-items: center;
    transition: all 0.4s;
    z-index: 2;
    cursor: pointer;
    user-select: none;
    padding: 0;
    position: absolute;
    top: 40px;
    left: -20px;
    box-shadow: 0 0 0 8px white;
  }

  .button:hover {
    background: #494b53;
    color: white;
  }

  @media (max-width: 864px) {
    .scrollable {
      padding: 20px 20px 40px;
    }
    h3 {
      margin-top: 0;
      font-size: 32px;
    }
    .button {
      position: relative;
      top: initial;
      left: initial;
      margin-top: 20px;
      margin-left: 20px;
    }
    aside {
      width: calc(100% - 12px);
    }
  }
</style>
